USE `sghis`;DROP PROCEDURE IF EXISTS `receipt_bill_procedure`;DELIMITER $$CREATE DEFINER=`root`@`localhost` PROCEDURE `receipt_bill_procedure`(IN `rn` VARCHAR(11))BEGIN   SELECT receipt.rn, receipt.receipt_content_datetime_paid, receipt.receipt_content_sum, receipt.receipt_type, 	receipt.receipt_content_payment_method, receipt.receipt_content_payer_name, receipt.receipt_serial_number, receipt.receipt_content_description, receipt.receipt_responsible, receipt.receipt_uid FROM receipt WHERE receipt.rn = rn    UNION    SELECT bill.rn, bill.bill_generation_datetime, bill.bill_generation_billable_sum_rm, null, null, null, bill.bill_print_id, null, bill.generation_responsible_uid, null FROM bill WHERE bill.rn = rn AND bill.bill_generation_datetime is not null;END$$DELIMITER ;DROP PROCEDURE IF EXISTS `transaction_records`;DELIMITER $$CREATE DEFINER=`root`@`localhost` PROCEDURE `transaction_records`(IN `pid` VARCHAR(64))BEGIN	SELECT receipt.rn, receipt.receipt_content_datetime_paid, receipt.receipt_content_sum, receipt.receipt_type, 	receipt.receipt_content_payment_method, receipt.receipt_content_payer_name, receipt.receipt_serial_number, receipt.receipt_content_description, receipt.receipt_responsible,  receipt.receipt_uid	FROM receipt	INNER JOIN patient_admission     ON patient_admission.rn = receipt.rn    INNER JOIN patient_information    ON patient_information.patient_uid = patient_admission.patient_uid    WHERE patient_information.patient_uid = pid        UNION         SELECT bill.rn, bill.bill_generation_datetime, bill.bill_generation_billable_sum_rm, null, null, null, bill.bill_print_id, null, bill.generation_responsible_uid, null    FROM bill     INNER JOIN patient_admission     ON patient_admission.rn = bill.rn    INNER JOIN patient_information    ON patient_information.patient_uid = patient_admission.patient_uid    WHERE patient_information.patient_uid = pid     AND bill.bill_generation_datetime is not null;     END$$DELIMITER ;